// author : CodeAlpha07 #include <bits/stdc++.h>using namespace std;//------------------------------------------------------------------------------------------------------------------------------------#define ll long long#define ull unsigned long long#define lld long double#define pii pair<int, int>#define pll pair<ll, ll>#define vi vector<int>#define vl vector<ll>#define vvi vector<vector<int>>#define vvl vector<vector<ll>>#define vpi vector<pii>#define vpl vector<pll>#define vin(a) for (int i = 0; i < (a).size(); ++i) cin >> a[i];#define vout(a) for (int i = 0; i < (a).size(); ++i) cout << a[i] << ' ';#define all(a) a.begin(), a.end()#define rall(a) a.rbegin(), a.rend()#define mii map<int, int>#define mci map<char, int>#define si set<int>#define sc set<char>#define sl set<ll>#define prql(a) priority_queue<a>#define prqs(a) priority_queue<a,vector<a>,greater<a>>#define pb push_back#define eb emplace_back#define ff first#define ss second#define sqrt(x) sqrtl(x)#define endl "\n"#define yes cout << "Yes" << endl;#define no cout << "No" << endl;#define M 1000000007#define M1 998244353#define fo(i,a,b) for(int i=(int)a;i<(int)b;i++)#define forr(i,a,b) for(int i=(int)a;i>=(int)b;i--)#define PI 3.1415926535897932384626433832795//------------------------------------------------------------------------------------------------------------------------------------const ll MAXN = 1010;const ll MOD = 1e9 + 7; vl fact(MAXN), invFact(MAXN); ll modExp(ll base, ll exp, ll mod) {    ll res = 1;    while (exp > 0) {        if (exp % 2 == 1) res = (res * base) % mod;        base = (base * base) % mod;        exp /= 2;    }    return res;} void precompute() {    fact[0] = 1;    for (int i = 1; i < MAXN; i++) {        fact[i] = (fact[i - 1] * i) % MOD;    }    invFact[MAXN - 1] = modExp(fact[MAXN - 1], MOD - 2, MOD);    for (int i = MAXN - 2; i >= 0; i--) {        invFact[i] = (invFact[i + 1] * (i + 1)) % MOD;    }} ll nCr(ll n, ll r) {    if (r > n || r < 0) return 0;    return fact[n] * invFact[r] % MOD * invFact[n - r] % MOD;} void solve() {    ll n, k;    cin >> n >> k;    vl v(n);    for (int i = 0; i < n; i++) cin >> v[i];     sort(v.rbegin(), v.rend());     vl suit(v.begin(), v.begin() + k);     map<ll, ll> mpneed, mp;    for (auto it : suit) mpneed[it]++;    for (auto it : v) mp[it]++;     ll ans = 1;    for (auto it : mpneed) {        ll ele = it.ff;        ll need = it.ss;        ans = (ans * nCr(mp[ele], need)) % MOD;    }     cout << ans << endl;} signed main() {    ios_base::sync_with_stdio(false);    cin.tie(0);    cout.tie(0);    precompute();    int T;    cin >> T;    while (T--) solve();}