#include <bits/stdc++.h>using namespace std; int n;vector<pair<int, int>> v; // v[i] = {ai, bi} bool check(int x) {		// Try to greedily select x friends.		// c = count of already selected friends; assigning the next person as the c-th poorest among selected.		int c = 0;		for (int i = 0; i < n; i++) {				// For the next selected person:				// poorer among invited = c  -> requires bi >= c				// richer among invited = x - c - 1 -> requires ai >= x - c - 1				if (v[i].second >= c && v[i].first >= x - c - 1) {						c++;				}		}		return c >= x; // Feasible if we can select at least x friends} int main() {		ios::sync_with_stdio(false);		cin.tie(nullptr); 		int tc;		cin >> tc;		while (tc--) {				cin >> n;				v.resize(n);				for (auto &p : v) cin >> p.first >> p.second; // Read (ai, bi) for each friend 				int l = 1, r = n, best = 1; // Binary search for the maximum feasible x				while (l <= r) {						int mid = (l + r) / 2; // Try inviting mid friends						if (check(mid)) {								best = mid; // mid is feasible; try larger								l = mid + 1;						} else {								r = mid - 1; // mid not feasible; try smaller						}				} 				cout << best << '\n';		} 		return 0;} /*Time Complexity per test case: O(n log n) — binary search over x with O(n) feasibility check.Space Complexity per test case: O(n) — to store (ai, bi).*/