// author : CodeAlpha07 #include <bits/stdc++.h>using namespace std;//------------------------------------------------------------------------------------------------------------------------------------#define ll long long#define ull unsigned long long#define lld long double#define pii pair<int, int>#define pll pair<ll, ll>#define vi vector<int>#define vl vector<ll>#define vvi vector<vector<int>>#define vvl vector<vector<ll>>#define vpi vector<pii>#define vpl vector<pll>#define vin(a) for (int i = 0; i < (a).size(); ++i) cin >> a[i];#define vout(a) for (int i = 0; i < (a).size(); ++i) cout << a[i] << ' ';#define all(a) a.begin(), a.end()#define rall(a) a.rbegin(), a.rend()#define mii map<int, int>#define mci map<char, int>#define si set<int>#define sc set<char>#define sl set<ll>#define prql(a) priority_queue<a>#define prqs(a) priority_queue<a,vector<a>,greater<a>>#define pb push_back#define eb emplace_back#define ff first#define ss second#define sqrt(x) sqrtl(x)#define endl "\n"#define yes cout << "Yes" << endl;#define no cout << "No" << endl;#define M 1000000007#define M1 998244353#define fo(i,a,b) for(int i=(int)a;i<(int)b;i++)#define forr(i,a,b) for(int i=(int)a;i>=(int)b;i--)#define PI 3.1415926535897932384626433832795//------------------------------------------------------------------------------------------------------------------------------------#ifndef ONLINE_JUDGE#define debug(x) cerr << #x << " "; _print(x); cerr << endl;#else#define debug(x)#endif void _print(ll t) { cerr << t; }void _print(int t) { cerr << t; }void _print(string t) { cerr << t; }void _print(char t) { cerr << t; }void _print(lld t) { cerr << t; }void _print(double t) { cerr << t; }void _print(ull t) { cerr << t; } template <class T, class V> void _print(pair<T, V> p);template <class T> void _print(vector<T> v);template <class T> void _print(set<T> v);template <class T, class V> void _print(map<T, V> v);template <class T> void _print(multiset<T> v);template <class T, class V> void _print(pair<T, V> p) { cerr << "{"; _print(p.first); cerr << ","; _print(p.second); cerr << "}"; }template <class T> void _print(vector<T> v) { cerr << "[ "; for (T i : v) { _print(i); cerr << ", "; } cerr << "]"; }template <class T> void _print(set<T> v) { cerr << "[ "; for (T i : v) { _print(i); cerr << ", "; } cerr << "]"; }template <class T> void _print(multiset<T> v) { cerr << "[ "; for (T i : v) { _print(i); cerr << ", "; } cerr << "]"; }template <class T, class V> void _print(map<T, V> v) { cerr << "[ "; for (auto i : v) { _print(i); cerr << ", "; } cerr << "]"; }//------------------------------------------------------------------------------------------------------------------------------------ll gcd(ll a, ll b) { if (b == 0) return a; return gcd(b, a % b); }ll lcm(ll a, ll b) { return a / gcd(a, b) * b; }string to_upper(string a) { for (int i = 0; i < (int)a.size(); ++i) if (a[i] >= 'a' && a[i] <= 'z') a[i] -= 'a' - 'A'; return a; }string to_lower(string a) { for (int i = 0; i < (int)a.size(); ++i) if (a[i] >= 'A' && a[i] <= 'Z') a[i] += 'a' - 'A'; return a; }bool prime(ll a) { if (a == 1) return 0; for (int i = 2; i <= round(sqrt(a)); ++i) if (a % i == 0) return 0; return 1; }bool palindrome(string s) { int n = s.length(); for (int i = 0; i < n / 2; i++) if (s[i] != s[n - 1 - i]) return 0; return 1; }ll cacl_mex(vl a){vl hashmap(a.size()+1,0);for(ll i=0;i<a.size();i++){if(a[i]<a.size())hashmap[a[i]]++;}for(ll i=0;i<=a.size();i++){if(hashmap[i]==0){return i;}}return -1;}ll binexp(ll a, ll b, ll p) { ll temp=1; while(b>0) { if (b&1) temp=((temp%p)*(a%p))%p; a=((a%p)*(a%p))%p; b>>=1;} return temp%p; }ll inv(ll x, ll p = M) { return x <= 1 ? x : p - p / x * inv(p % x) % p; }ll countDigit(ll n) { return n / 10 == 0 ? 1 : 1 + countDigit(n / 10); }ll modinv(ll x, ll md) { return binexp(x, md - 2, md);}void unduplicate(vi &vec) { sort(vec.begin(), vec.end()); vec.erase(unique(vec.begin(), vec.end()), vec.end()); }vector<bool> isPrime;void sieve() {int n=1e6;isPrime.resize(n+1,true);isPrime[0] = isPrime[1] = false;for (int p = 2; p * p <= n; ++p) {if (isPrime[p]) {for (int i = p * p; i <= n; i += p) {isPrime[i] = false;}}}} //------------------------------------------------------------------------------------------------------------------------------------ void solve(){    int n;    cin>>n;    vi a(n);    vin(a);        // Worst case ans is already 1 (when we remove the whole array) ; so we will remove the code into chunks that do not contain the 0    // since only abs(+-2) changes the magnitude ; so will check the seg with most 2s and product positive        // best: max count of |2| kept so far (proxy for product magnitude)    // bestl: elements removed from the beginning to achieve best    // bestr: elements removed from the end to achieve best    int best = 0, bestl = n, bestr = 0;    int l=0;    while (l<n) {        // Split the array by zeros; zeros reset the product to 0 and break segments        if(a[l]==0) {l++; continue;}         // Process a maximal non-zero segment [l..r]        int r = l-1, two_cnt=0, neg=1;  // tc: count of |2|, neg: sign parity        while (r+1<n && a[r+1] != 0) {            r++;            if (abs(a[r])==2) two_cnt++;       // only ±2 affect magnitude            if (a[r]<0) neg*=-1;        // track sign of product on this segment        }         // the product is positive ; easy peasy        if(neg==1){            if(two_cnt>best){                best = two_cnt;                bestl = l;                 bestr = (n-1)-r;            }            l=r+1; // move to next segment             continue;        }         // the product is negative : neg==-1 : neg->odd        // Option 1 : We will check to remove the segment from left till first negative [l..... first negative]        int temp_tc = two_cnt, temp_l = l - 1;        do {            temp_l++;            if (abs(a[temp_l]) == 2) temp_tc--;        } while (a[temp_l] > 0);         if (temp_tc > best) {            best = temp_tc;            bestl = temp_l + 1;            bestr = (n - 1) - r;        }        // Option 2 : We will check to remove the segment from right till first negative [first negative......r]        temp_tc = two_cnt; int temp_r = r + 1;        do {            temp_r--;            if (abs(a[temp_r]) == 2) temp_tc--;        } while (a[temp_r] > 0);         if (temp_tc > best) {            best = temp_tc;            bestl = l;            bestr = n - temp_r;        }           l=r+1;    }    cout<<bestl<<" "<<bestr<<endl;} signed main(){    ios_base::sync_with_stdio(false); cin.tie(0); cout.tie(0);    #ifndef ONLINE_JUDGE    #endif    int T;    cin >> T;    while (T--)    {        solve();    }}